mapper.select:
SELECT posting.id , posting.deleted , posting.created_at as createdAt , posting.modified_at as modifiedAt
, posting.user_id as userId , posting.title , posting.content , posting.img
, user.username as userUsername , user.name as userName FROM posting LEFT JOIN user ON posting.user_id = user.id WHERE posting.id = #{id}

mapper.sql:
WHERE posting.id is not null
AND posting.deleted = #{deleted}
AND posting.user_id = #{userId}
AND posting.title LIKE CONCAT('%', #{title}, '%')

mapper.select:
SELECT posting.id FROM posting
AND #{cursor} > posting.id
ORDER BY posting.id DESC
AND #{cursorsearch} > CONCAT(posting.title, '_', posting.id)
ORDER BY CONCAT(posting.title, '_', posting.id) DESC
AND posting.id > #{cursor}
ORDER BY posting.id ASC
AND CONCAT(posting.title, '_', posting.id) > #{cursorsearch}
ORDER BY CONCAT(posting.title, '_', posting.id) ASC
LIMIT 0, #{perpage}
